#include<stdio.h>
int main()
{
	int n, i, j;
	int a[10001];
	a[1] = 1;
	a[2] = 2;
	for (i = 3; i <= 10000; i++)
	{
		a[i] = (a[i - 1] + a[i - 2]);
		if (a[i] >= 10000)
			a[i] = a[i] % 10000;
	}
	while (scanf("%d", &n) != EOF)
	{
		for (i = 1; i <= n; i++)
		{
			scanf("%d", &j);
			printf("%04d", a[j]);
		}
		printf("\n");
	}
	return 0;
}